import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaGoogle, FaGithub } from "react-icons/fa";
import "./login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [popupMessage, setPopupMessage] = useState("");
  const navigate = useNavigate();

  // OAuth login (Opens a new window)
  const handleOAuthLogin = (provider) => {
    let authUrl = "";

    if (provider === "google") {
      authUrl = `https://accounts.google.com/o/oauth2/auth?client_id=YOUR_GOOGLE_CLIENT_ID&redirect_uri=http://localhost:3000&response_type=token&scope=email`;
    } else if (provider === "github") {
      authUrl = `https://github.com/login/oauth/authorize?client_id=YOUR_GITHUB_CLIENT_ID&redirect_uri=http://localhost:3000`;
    }

    window.open(authUrl, "_blank", "width=500,height=600");
  };

  const handleLogin = () => {
    const storedUser = JSON.parse(localStorage.getItem("user")); // Get stored user

    if (storedUser && storedUser.email === email && storedUser.password === password) {
      setPopupMessage("✅ Login Successful! Redirecting...");
      setTimeout(() => {
        navigate("/otp"); // Redirect to OTP page
      }, 2000);
    } else {
      setPopupMessage("❌ User not registered! Please sign up.");
    }

    setTimeout(() => setPopupMessage(""), 2000); // Hide popup after 2 sec
  };

  return (
    <div className="login-card">
      <h2>Login to Your Account</h2>

      <div className="social-login">
        <button className="social-btn google-btn" onClick={() => handleOAuthLogin("google")}>
          <FaGoogle className="icon" /> Google
        </button>
        <button className="social-btn github-btn" onClick={() => handleOAuthLogin("github")}>
          <FaGithub className="icon" /> GitHub
        </button>
      </div>

      <div className="separator"><span>or continue with email</span></div>

      <div className="input-group">
        <label>Email</label>
        <input type="email" placeholder="Enter your email" value={email} onChange={(e) => setEmail(e.target.value)} />
      </div>

      <div className="input-group">
        <label>Password</label>
        <input type="password" placeholder="Enter your password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </div>

      <div className="options">
        <label>
          <input type="checkbox" /> Remember me
        </label>
        <a href="#">Forgot Password?</a>
      </div>

      <button className="login-btn" onClick={handleLogin}>Login</button>

      <p className="create-account">
        Don't have an account? <a href="/signup">Sign up</a>
      </p>

      {popupMessage && <div className="popup"><p>{popupMessage}</p></div>}
    </div>
  );
};

export default Login;
