import React from "react";
import "./home.css";

const Home = () => {
  return (
    <div className="home-container">
      <h2 className="title">Please select your <span className="highlight">Role</span></h2>
      <div className="role-selection">
        <div className="role-card" onClick={() => (window.location.href = "/admin-login")}>
          <img src="/images/admin.png" alt="Admin" className="role-image" />
          <p>Admin</p>
        </div>
        <div className="role-card" onClick={() => (window.location.href = "/user-login")}>
          <img src="/images/user.png" alt="User" className="role-image" />
          <p>User</p>
        </div>
      </div>
      <button className="continue-button">Continue</button>
    </div>
  );
};

export default Home;
