import React, { useState } from 'react';
import "./message.css"; 
const com1logo = "/images/com1logo.png";
const plusIcon = "/images/plus.png";
const threeDots = "/images/three-dots.png";


function Message() {
  const [selectedChat, setSelectedChat] = useState('General Chat');
  const [message, setMessage] = useState('');
  const [showInvite, setShowInvite] = useState(false);
  const [showCreateGroup, setShowCreateGroup] = useState(false);
  const [newGroupName, setNewGroupName] = useState('');
  const [newGroupMembers, setNewGroupMembers] = useState(['Ankush']);
  const [newGroupChatType, setNewGroupChatType] = useState('Public');
  const [newGroupDescription, setNewGroupDescription] = useState('');
  const [newMemberName, setNewMemberName] = useState('');
  const [chats, setChats] = useState({
    'General Chat': [
      { sender: 'AK', message: 'hi', time: '9:46 am' },
      { sender: 'MN', message: 'hi', time: '9:47 am' },
    ],
    'Group Chat #1': [
      { sender: 'AK', message: 'Hello Group!', time: '9:35 am' },
      { sender: 'MN', message: 'Hi everyone!', time: '9:36 am' },
    ],
    'Ankush (it\'s you)': [
      { sender: 'AK', message: 'Hey Ankush!', time: 'Tue' },
      { sender: 'MN', message: 'Hello!', time: 'Tue' },
    ],
    'Mani': [
      { sender: 'AK', message: 'Hi Mani!', time: 'Mon' },
      { sender: 'MN', message: 'Hey!', time: 'Mon' },
    ],
  });

  const handleSendMessage = () => {
    if (message.trim() !== '') {
      const newMessage = { sender: 'You', message, time: 'Now' };
      setChats({
        ...chats,
        [selectedChat]: [...chats[selectedChat], newMessage],
      });
      setMessage('');
    }
  };

  const handleInviteClick = () => {
    setShowInvite(!showInvite);
  };

  const handleCopyLink = () => {
    navigator.clipboard.writeText(`http://zidio.com/${selectedChat.toLowerCase().replace(/ /g, '')}/?invite`);
    alert('Link copied to clipboard!');
  };

  const handleCreateGroupClick = () => {
    setShowCreateGroup(!showCreateGroup);
  };

  const handleAddMember = () => {
    if (newMemberName.trim() !== '') {
      setNewGroupMembers([...newGroupMembers, newMemberName]);
      setNewMemberName('');
    }
  };

  const handleCreateGroup = () => {
    if (newGroupName.trim() !== '') {
      setChats({
        ...chats,
        [newGroupName]: [],
      });
      setShowCreateGroup(false);
      setNewGroupName('');
      setNewGroupMembers(['Ankush']);
      setNewGroupChatType('Public');
      setNewGroupDescription('');
    } else {
      alert('Group name is required');
    }
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className="w-1/4 bg-gray-200 p-4 border-gray-300 border-2">
        <div className="flex items-center mb-4">
          <img src={com1logo} alt="Zidio Logo" className="h-8 mr-2 rounded-3xl" />
          <input
            type="text"
            placeholder="Chat or search"
            className="flex-grow p-2 bg-white border border-gray-300 rounded-3xl"
          />
          <button onClick={handleCreateGroupClick} className="ml-2 p-2 rounded bg-blue-300 text-white">
            <img src={plusIcon} alt="Plus Icon" className="h-6 w-6 " />
          </button>
        </div>
        <div className="flex flex-col gap-2">
          {Object.keys(chats).map(chat => (
            <div
              key={chat}
              className={`p-2  flex items-center border-gray-100 border-2 rounded-xl ${selectedChat === chat ? 'bg-blue-100' : 'bg-white'}`}
              onClick={() => setSelectedChat(chat)}
            >
              <div className="flex-grow">{chat}</div>
              <div className="text-sm text-gray-500">{chats[chat][0]?.time}</div>
              <button className="ml-2 p-2 rounded bg-transparent">
                <img src={threeDotsIcon} alt="Three Dots Icon" className="h-6 w-6" />
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="w-3/4 flex flex-col relative">
        <div className="flex items-center justify-between bg-blue-100 p-4 border-gray-100 border-2 rounded-xl">
          <div>{selectedChat}</div>
          <div className="flex items-center gap-2">
            <button onClick={handleInviteClick} className="p-2 rounded bg-gray-300">👤</button>
            <button className="p-2 rounded bg-gray-300">🔍</button>
          </div>
        </div>
        {showInvite && (
          <div className="absolute top-16 right-4 bg-blue-100 p-4 rounded-lg shadow-lg">
            <div className="mb-2">Quick invite</div>
            <input
              type="text"
              value={`http://zidio.com/${selectedChat.toLowerCase().replace(/ /g, '')}/?invite`}
              readOnly
              className="w-full p-2 mb-2 bg-gray-100 rounded"
            />
            <button onClick={handleCopyLink} className="w-full p-2 bg-blue-500 text-white rounded">COPY LINK</button>
          </div>
        )}
        {showCreateGroup && (
          <div className="absolute top-8 bg-blue-100 p-4 rounded-3xl shadow-2xl w-80 border-2 border-gray-300">
            <div className="mb-2 text-lg font-bold">Create New Group</div>
            <div className="mb-2">
              <label className="block text-sm font-medium text-gray-700">Group Name</label>
              <input
                type="text"
                value={newGroupName}
                onChange={(e) => setNewGroupName(e.target.value)}
                className="w-full p-2 bg-gray-100 rounded"
                placeholder="Group Name"
              />
            </div>
            <div className="mb-2">
              <label className="block text-sm font-medium text-gray-700">Members</label>
              <div className="flex items-center gap-2 flex-wrap">
                {newGroupMembers.map((member, index) => (
                  <div key={index} className="bg-blue-500 text-white px-2 py-1 rounded">{member}</div>
                ))}
                <input
                  type="text"
                  value={newMemberName}
                  onChange={(e) => setNewMemberName(e.target.value)}
                  placeholder="Add member"
                  className="p-2 rounded bg-gray-100 border border-gray-300"
                />
                <button onClick={handleAddMember} className="p-1 rounded bg-blue-500 text-white">Add</button>
              </div>
            </div>
            <div className="mb-2">
              <label className="block text-sm font-medium text-gray-700">Chat type</label>
              <div className="flex items-center gap-2">
                <input
                  type="radio"
                  name="chatType"
                  value="Public"
                  checked={newGroupChatType === 'Public'}
                  onChange={(e) => setNewGroupChatType(e.target.value)}
                  className="mr-1"
                /> Public
                <input
                  type="radio"
                  name="chatType"
                  value="Private"
                  checked={newGroupChatType === 'Private'}
                  onChange={(e) => setNewGroupChatType(e.target.value)}
                  className="ml-4 mr-1"
                /> Private
                <input
                  type="radio"
                  name="chatType"
                  value="Admin"
                  checked={newGroupChatType === 'Admin'}
                  onChange={(e) => setNewGroupChatType(e.target.value)}
                  className="ml-4 mr-1"
                /> Admin
              </div>
            </div>
            <div className="mb-2">
              <label className="block text-sm font-medium text-gray-700">Description</label>
              <textarea
                value={newGroupDescription}
                onChange={(e) => setNewGroupDescription(e.target.value)}
                className="w-full p-2 bg-gray-100 rounded"
                placeholder="Description"
              ></textarea>
            </div>
            <button onClick={handleCreateGroup} className="w-full p-2 bg-blue-500 text-white rounded">Create Group</button>
          </div>
        )}
        <div className="flex-grow p-4 bg-white">
          {chats[selectedChat].map((chat, index) => (
            <div key={index} className={`flex items-end mb-4 ${chat.sender === 'MN' ? 'justify-end' : ''}`}>
              {chat.sender !== 'MN' && <div className="bg-blue-100 p-2 rounded-lg">{chat.message}</div>}
              <div className="ml-2 text-sm text-gray-500">{chat.sender}</div>
              {chat.sender === 'MN' && <div className="bg-gray-300 p-2 rounded-lg">{chat.message}</div>}
            </div>
          ))}
        </div>
        <div className="flex items-center p-4 bg-gray-200">
          <input
            type="text"
            placeholder="Type a message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            className="flex-grow p-2 rounded bg-white border border-gray-300"
          />
          <button onClick={handleSendMessage} className="ml-2 p-2 rounded bg-blue-500 text-white">😊</button>
          <button onClick={handleSendMessage} className="ml-2 p-2 rounded bg-blue-500 text-white">➤</button>
        </div>
      </div>
    </div>
  );
}

export default Message;
