import React, { useState } from "react";
import Navbar from "../navbar/Navbar";
import Login from "../login/Login";
import Signup from "../signup/Signup";
import "./home.css";

const Home = () => {
  const [showLogin, setShowLogin] = useState(false);
  const [showSignup, setShowSignup] = useState(false);

  return (
    <div className="home-container">
      {/* Navbar with Login & Signup Buttons */}
      <Navbar onLoginClick={() => setShowLogin(true)} onSignupClick={() => setShowSignup(true)} />

      {/* Home Content */}
      <div className="content">
        <h1>Transform Task Management with Zidio</h1>
        <p>
          Harness Zidio Task Management to enhance efficiency and collaboration. Our intuitive platform allows for effortless task organization, real-time progress tracking, and seamless teamwork.
        </p>
      </div>

      {/* Image */}
      <div className="image-container">
        <img src="/images/image.png" alt="Home" className="home-image" />
      </div>

            {/* Feature Section */}
            <div className="features-section">
        <h2 className="features-header">
          Experience the transformative capabilities of Zidio Task Management
        </h2>

        <div className="cards-container">
          {/* Card 1 - Kanban Boards */}
          <div className="card">
            <img src="/images/kanban.jpg" alt="Kanban Boards" className="card-image" />
            <h3>Robust Task Organization with Kanban Boards</h3>
            <p>Visualize your workflows and prioritize tasks effectively, ensuring your team is always aligned.</p>
          </div>

          {/* Card 2 - Progress Tracking */}
          <div className="card">
            <img src="/images/progress.jpg" alt="Progress Tracking" className="card-image" />
            <h3>Real-Time Progress Tracking</h3>
            <p>Stay up-to-date with your projects and make swift adjustments to keep everything on course.</p>
          </div>

          {/* Card 3 - Seamless Integrations */}
          <div className="card">
            <img src="/images/integrations.jpg" alt="Seamless Integrations" className="card-image" />
            <h3>Seamless Integrations</h3>
            <p>Integrate with the tools you already use, simplifying your workflow and reducing app-switching.</p>
          </div>
        </div>
      </div>


      {/* Login Popup */}
      {showLogin && <Login onClose={() => setShowLogin(false)} />}

      {/* Signup Popup */}
      {showSignup && <Signup onClose={() => setShowSignup(false)} />}
    </div>

  );
};

export default Home;
