import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./login.css";
import { FaGoogle, FaGithub } from "react-icons/fa";

const Login = () => {
  const [showPopup, setShowPopup] = useState(false);
  const navigate = useNavigate();

  const handleLogin = () => {
    // Dummy authentication check (Replace with real API call)
    setShowPopup(true);
    setTimeout(() => {
      setShowPopup(false);
      navigate("/otp"); // Redirect to OTP Verification Page
    }, 2000); // Delay navigation after 2 seconds
  };

  return (
    <div className="login-card">
      <h2>Login to Your Account</h2>

      {/* Social Login */}
      <div className="social-login">
        <button className="social-btn google-btn">
          <FaGoogle className="icon" /> Google
        </button>
        <button className="social-btn github-btn">
          <FaGithub className="icon" /> GitHub
        </button>
      </div>

      {/* Separator */}
      <div className="separator"><span>or continue with email</span></div>

      {/* Email & Password Fields */}
      <div className="input-group">
        <label>Email</label>
        <input type="email" placeholder="Enter your email" />
      </div>

      <div className="input-group">
        <label>Password</label>
        <input type="password" placeholder="Enter your password" />
      </div>

      {/* Remember Me & Forgot Password */}
      <div className="options">
        <label>
          <input type="checkbox" /> Remember me
        </label>
        <a href="#">Forgot Password?</a>
      </div>

      {/* Login Button */}
      <button className="login-btn" onClick={handleLogin}>Login</button>

      {/* Create Account */}
      <p className="create-account">
        Don't have an account? <a href="/signup">Sign up</a>
      </p>

      {/* Login Success Popup */}
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <h3>âœ… Login Successful!</h3>
            <p>Redirecting to OTP verification...</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Login;
