import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaEnvelope, FaLock } from "react-icons/fa";
import "./login.css";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [popupMessage, setPopupMessage] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    if (!email || !password) {
      setPopupMessage("⚠️ Please fill all fields!");
      setTimeout(() => setPopupMessage(""), 2000);
      return;
    }

    const existingUsers = JSON.parse(localStorage.getItem("users")) || [];
    const foundUser = existingUsers.find(
      (user) => user.email === email && user.password === password
    );

    if (foundUser) {
      // Store full name in localStorage
      localStorage.setItem("userData", JSON.stringify({ fullName: foundUser.name }));

      setPopupMessage(`✅ Welcome back, ${foundUser.name}!`);
      
      setTimeout(() => {
        navigate("/dashboard"); // Redirect to dashboard
      }, 1500);
    } else {
      setPopupMessage("❌ Invalid email or password!");
    }

    setTimeout(() => setPopupMessage(""), 2000);
  };

  return (
    <div className="form-container">
      <div className="form-card">
        <h2>Log in to your account</h2>

        <div className="input-group">
          <i><FaEnvelope /></i>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>

        <div className="input-group">
          <i><FaLock /></i>
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>

        <button className="login-btn" onClick={handleLogin}>Log In</button>

        <p className="form-link">
          Don't have an account? <a href="/signup">Sign up</a>
        </p>

        {popupMessage && <div className="popup"><p>{popupMessage}</p></div>}
      </div>
    </div>
  );
}
