import React, { useState, useEffect } from "react";
import "./navbar.css";
import { Link, useNavigate } from "react-router-dom";
import {
  FaSearch,
  FaUsers,
  FaChartBar,
  FaEnvelope,
  FaUserPlus,
  FaUserCircle,
  FaCopy,
} from "react-icons/fa";

const Navbar = () => {
  const [showPeopleDropdown, setShowPeopleDropdown] = useState(false);
  const [showReportsDropdown, setShowReportsDropdown] = useState(false);
  const [showInviteDropdown, setShowInviteDropdown] = useState(false);
  const [showUserDropdown, setShowUserDropdown] = useState(false);
  const [fullName, setFullName] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [quickRegistration, setQuickRegistration] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUserData = localStorage.getItem("userData");
    if (storedUserData) {
      const { fullName } = JSON.parse(storedUserData);
      setFullName(fullName);
      setIsLoggedIn(true);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("userData");
    setIsLoggedIn(false);
    navigate("/");
  };

  const inviteLink = "https://zidio.com/generate/link/?invite";

  const handleCopyLink = () => {
    navigator.clipboard.writeText(inviteLink);
    alert("Invite link copied to clipboard!");
  };

  return (
    <nav className="navbar">
      {!isLoggedIn ? (
        <div className="nav-auth-container">
          <h1 className="app-title">Zidio Task Management</h1>
          <div className="auth-buttons">
            <button className="auth-button" onClick={() => navigate("/login")}>
              Login
            </button>
            <button className="auth-button" onClick={() => navigate("/signup")}>
              Sign Up
            </button>
          </div>
        </div>
      ) : (
        <>
          <div className="nav-left">
            <img
              src="/images/zidio_development_logo.jpg"
              alt="Zidio Logo"
              className="logo"
            />
          </div>

          <div className="nav-center">
            <div className="dropdown">
              <button
                className="dropdown-button"
                onClick={() => setShowPeopleDropdown(!showPeopleDropdown)}
              >
                People <FaUsers className="dropdown-icon" />
              </button>
              {showPeopleDropdown && (
                <div className="dropdown-menu">
                  <button>View People</button>
                  <button>Manage Teams</button>
                  <button>Add People</button>
                </div>
              )}
            </div>

            <div className="dropdown">
              <button
                className="dropdown-button"
                onClick={() => setShowReportsDropdown(!showReportsDropdown)}
              >
                Report <FaChartBar className="dropdown-icon" />
              </button>
              {showReportsDropdown && (
                <div className="dropdown-menu">
                  <button>Weekly Reports</button>
                  <button>Monthly Analysis</button>
                  <button>Annual Summary</button>
                </div>
              )}
            </div>

            <Link to="/messages" className="nav-icon-link">
              <FaEnvelope className="nav-icon" title="Messages" />
            </Link>

            <div className="search-container">
              <input type="text" placeholder="Search" className="search-input" />
              <FaSearch className="search-icon" />
            </div>

            <div className="dropdown">
              <button
                className="dropdown-button"
                onClick={() => setShowInviteDropdown(!showInviteDropdown)}
              >
                Invite <FaUserPlus className="dropdown-icon" />
              </button>
              {showInviteDropdown && (
                <div className="dropdown-menu invite-dropdown">
                  <p className="invite-title">ðŸ”˜ Invite via link</p>
                  
                  <label className="invite-option">
                    <input
                      type="checkbox"
                      checked={quickRegistration}
                      onChange={() => setQuickRegistration(!quickRegistration)}
                    />
                    Allow quick Registration
                  </label>
                  
                  <div className="invite-link-container">
                    <input type="text" value={inviteLink} readOnly className="invite-link-input" />
                    <button className="copy-button" onClick={handleCopyLink}>
                      <FaCopy />
                    </button>
                  </div>

                  <label className="update-link">
                    <input type="checkbox" /> Update link
                  </label>
                </div>
              )}
            </div>
          </div>

          <div className="nav-right">
            <div className="user-icon-container">
              <FaUserCircle
                className="user-icon"
                title="User Profile"
                onClick={() => setShowUserDropdown(!showUserDropdown)}
              />
              {showUserDropdown && (
                <div className="dropdown-menu user-dropdown">
                  <p className="dropdown-item">Hi, {fullName}</p>
                  <hr />
                  <p className="dropdown-item" onClick={() => navigate("/settings")}>
                    Settings
                  </p>
                  <p className="dropdown-item logout" onClick={handleLogout}>
                    Logout
                  </p>
                </div>
              )}
            </div>
          </div>
        </>
      )}
    </nav>
  );
};

export default Navbar;
