import React, { useState } from "react";
import "./navbar.css";
import {
  FaSearch,
  FaUsers,
  FaChartBar,
  FaEnvelope,
  FaUserPlus,
  FaUserCircle,
} from "react-icons/fa";

const Navbar = () => {
  const [showPeopleDropdown, setShowPeopleDropdown] = useState(false);
  const [showReportsDropdown, setShowReportsDropdown] = useState(false);
  const [showInviteDropdown, setShowInviteDropdown] = useState(false);

  return (
    <nav className="navbar">
      {/* Left side: Logo */}
      <div className="nav-left">
        <img
          src="/images/zidio_development_logo.jpg"
          alt="Zidio Logo"
          className="logo"
        />
      </div>

      {/* Center: Buttons, Icons, and Search */}
      <div className="nav-center">
        {/* People Dropdown */}
        <div className="dropdown">
          <button
            className="dropdown-button"
            onClick={() => setShowPeopleDropdown(!showPeopleDropdown)}
          >
            People <FaUsers className="dropdown-icon" />
          </button>
          {showPeopleDropdown && (
            <div className="dropdown-menu">
              <button>View People</button>
              <button>Manage Teams</button>
              <button>Add People</button>
            </div>
          )}
        </div>

        {/* Reports Dropdown */}
        <div className="dropdown">
          <button
            className="dropdown-button"
            onClick={() => setShowReportsDropdown(!showReportsDropdown)}
          >
            Report <FaChartBar className="dropdown-icon" />
          </button>
          {showReportsDropdown && (
            <div className="dropdown-menu">
              <button>Weekly Reports</button>
              <button>Monthly Analysis</button>
              <button>Annual Summary</button>
            </div>
          )}
        </div>

        {/* Messages Icon */}
        <FaEnvelope className="nav-icon" title="Messages" />

        {/* In Progress Tab */}
        <div className="tab">
          In Progress
        </div>

        {/* Search Input */}
        <div className="search-container">
          <input type="text" placeholder="Search" className="search-input" />
          <FaSearch className="search-icon" />
        </div>

        {/* Invite Dropdown */}
        <div className="dropdown">
          <button
            className="dropdown-button"
            onClick={() => setShowInviteDropdown(!showInviteDropdown)}
          >
            Invite <FaUserPlus className="dropdown-icon" />
          </button>
          {showInviteDropdown && (
            <div className="dropdown-menu">
              <button>Invite via Email</button>
              <button>Invite via Link</button>
            </div>
          )}
        </div>
      </div>

      {/* Right side: User Profile Icon */}
      <div className="nav-right">
        <FaUserCircle className="user-icon" title="User Profile" />
      </div>
    </nav>
  );
};

export default Navbar;
