import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaUser, FaEnvelope, FaLock, FaGoogle, FaGithub } from "react-icons/fa";
import "./signup.css";

export default function Signup() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const navigate = useNavigate();

  // Google OAuth login (Opens a new window)
  const handleGoogleLogin = () => {
    window.open(
      `https://accounts.google.com/o/oauth2/auth?
      client_id=rajarajeswari2498@gmail.com&
      redirect_uri=http://localhost:3000&
      response_type=code&
      scope=email&
      access_type=online`,
      "_blank",
      "width=500,height=600"
    );
  };
  
  
  

  // GitHub OAuth login (Opens a new window)
  const handleGithubLogin = () => {
    window.open(
      "https://github.com/login/oauth/authorize?client_id=YOUR_GITHUB_CLIENT_ID&redirect_uri=http://localhost:3000",
      "_blank",
      "width=500,height=600"
    );
  };

  return (
    <div className="form-container">
      <div className="form-card">
        <h2>Sign up an account</h2>
        <div className="social-login">
          <button className="social-btn google-btn" onClick={handleGoogleLogin}>
            <FaGoogle className="icon" /> Google
          </button>
          <button className="social-btn github-btn" onClick={handleGithubLogin}>
            <FaGithub className="icon" /> GitHub
          </button>
        </div>

        <div className="separator"><span>or continue with email</span></div>

        <div className="input-group">
          <i><FaUser /></i>
          <input type="text" placeholder="Full Name" value={name} onChange={(e) => setName(e.target.value)} />
        </div>

        <div className="input-group">
          <i><FaEnvelope /></i>
          <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>

        <div className="input-group">
          <i><FaLock /></i>
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>

        <div className="input-group">
          <i><FaLock /></i>
          <input type="password" placeholder="Confirm Password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>

        <button className="signup-btn">Sign Up</button>

        <p className="form-link">
          Already have an account?  
          <span className="login-link" onClick={() => navigate("/")}> Log in</span>
        </p>
      </div>
    </div>
  );
}
