import React, { useState } from "react";
import "./navbar.css";
import {
  FaSearch,
  FaUsers,
  FaChartBar,
  FaEnvelope,
  FaUserPlus,
  FaUserCircle,
} from "react-icons/fa";

const Navbar = () => {
  // State for dropdowns
  const [showPeopleDropdown, setShowPeopleDropdown] = useState(false);
  const [showReportsDropdown, setShowReportsDropdown] = useState(false);
  const [showInviteDropdown, setShowInviteDropdown] = useState(false);

  return (
    <nav className="navbar">
      {/* Left side: Logo */}
      <div className="nav-left">
        <img
          src="/images/zidio_development_logo.jpg"
          alt="Zidio Logo"
          className="logo"
        />
      </div>

      {/* Center: Icons & Dropdowns */}
      <div className="nav-center">
        {/* Search Icon */}
        <FaSearch className="nav-icon" title="Search" />

        {/* People Dropdown */}
        <div className="dropdown">
          <button
            className="dropdown-button"
            onClick={() => setShowPeopleDropdown(!showPeopleDropdown)}
          >
            <FaUsers title="People" />
          </button>
          {showPeopleDropdown && (
            <div className="dropdown-menu">
              <a href="#">View People</a>
              <a href="#">Manage Teams</a>
              <a href="#">Add People</a>
            </div>
          )}
        </div>

        {/* Reports Dropdown */}
        <div className="dropdown">
          <button
            className="dropdown-button"
            onClick={() => setShowReportsDropdown(!showReportsDropdown)}
          >
            <FaChartBar title="Reports" />
          </button>
          {showReportsDropdown && (
            <div className="dropdown-menu">
              <a href="#">Weekly Reports</a>
              <a href="#">Monthly Analysis</a>
              <a href="#">Annual Summary</a>
            </div>
          )}
        </div>

        {/* Messages Icon */}
        <FaEnvelope className="nav-icon" title="Messages" />

        {/* Invite Dropdown */}
        <div className="dropdown">
          <button
            className="dropdown-button"
            onClick={() => setShowInviteDropdown(!showInviteDropdown)}
          >
            <FaUserPlus title="Invite" />
          </button>
          {showInviteDropdown && (
            <div className="dropdown-menu">
              <a href="#">Invite via Email</a>
              <a href="#">Invite via Link</a>
            </div>
          )}
        </div>
      </div>

      {/* Right side: User Profile Icon */}
      <div className="nav-right">
        <FaUserCircle className="user-icon" title="User Profile" />
      </div>
    </nav>
  );
};

export default Navbar;
