import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import Login from "./components/login/Login";
import Signup from "./components/signup/Signup";
import OTPVerification from "./components/otpverification/OTPVerification";
import Dashboard from "./components/dashboard/Dashboard";
import Messages from "./components/message/Message"; // Import Messages component

function App() {
  const [isVerified, setIsVerified] = useState(false); // State to track OTP verification

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/otp" element={<OTPVerification setIsVerified={setIsVerified} />} />
        <Route 
          path="/dashboard" 
          element={isVerified ? <Dashboard /> : <Navigate to="/otp" />} 
        />
        <Route path="/message" element={<Message />} /> {/* Added Messages Route */}
      </Routes>
    </Router>
  );
}

export default App;
