import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import Login from "./components/login/Login";
import Vlogin from "./components/vlogin/Vlogin";
import Signup from "./components/signup/Signup";
import OTPVerification from "./components/otpv/OTPVerification";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [otpRequired, setOtpRequired] = useState(false);

  return (
    <Router>
      <Routes>
        <Route 
          path="/" 
          element={<Login setIsAuthenticated={setIsAuthenticated} setOtpRequired={setOtpRequired} />} 
        />
        <Route path="/vlogin" element={<Vlogin />} />
        <Route path="/signup" element={<Signup />} />
        <Route 
          path="/otp" 
          element={otpRequired ? <OTPVerification setIsAuthenticated={setIsAuthenticated} /> : <Navigate to="/" />} 
        />
      </Routes>
    </Router>
  );
}

export default App;
