import React, { useState } from "react";
import Navbar from "../navbar/Navbar";
import Login from "../login/Login";
import Signup from "../signup/Signup";
import "./home.css";

const Home = () => {
  const [showLogin, setShowLogin] = useState(false);
  const [showSignup, setShowSignup] = useState(false);

  return (
    <div className="home-container">
      {/* Navbar with Login & Signup Buttons */}
      <Navbar
        onLoginClick={() => setShowLogin(true)}
        onSignupClick={() => setShowSignup(true)}
      />

      {/* Home Content */}
      <div className="content">
        <h1>Transform Task Management with Zidio</h1>
        <p>
          Harness Zidio Task Management to enhance efficiency and collaboration.
          Our intuitive platform allows for effortless task organization,
          real-time progress tracking, and seamless teamwork.
        </p>
        <button className="btn-secondary">Get Started</button>
      </div>

      {/* Image */}
      <div className="image-container">
        <img src="/images/image.png" alt="Home" className="home-image" />
      </div>

      {/* Feature Section */}
      <div className="features-section">
        <h2 className="features-header">
          Experience the Transformative Capabilities of Zidio Task Management
        </h2>

        <div className="cards-container">
          {[
            {
              src: "/images/card1.png",
              alt: "Kanban Boards",
              title: "Robust Task Organization with Kanban Boards",
              text: "Visualize your workflows and prioritize tasks effectively, ensuring your team is always aligned."
            },
            {
              src: "/images/card2.png",
              alt: "Progress Tracking",
              title: "Real-Time Progress Tracking",
              text: "Stay up-to-date with your projects and make swift adjustments to keep everything on course."
            },
            {
              src: "/images/card3.png",
              alt: "Seamless Integrations",
              title: "Seamless Integrations",
              text: "Integrate with the tools you already use, simplifying your workflow and reducing app-switching."
            }
          ].map((card, index) => (
            <div key={index} className="card">
              <img src={card.src} alt={card.alt} className="card-image" />
              <h3>{card.title}</h3>
              <p>{card.text}</p>
            </div>
          ))}
        </div>

        <div className="features-text">
          <h1>Empower Your Team with Zidio Task Management</h1>
          <p>
            Our platform enhances efficiency and collaboration, allowing you to take control of your projects effortlessly.
          </p>

          <div className="features-list">
            {[
              {
                title: "1. Dynamic Kanban Boards",
                text: "Visualize your workflow and prioritize tasks with clarity, enhancing accountability among team members."
              },
              {
                title: "2. Real-Time Updates",
                text: "Stay informed as projects evolve, ensuring your team remains synchronized and adaptable."
              },
              {
                title: "3. Seamless Integrations",
                text: "Integrate with existing tools to streamline processes and maintain productivity without interruption."
              }
            ].map((feature, index) => (
              <div key={index} className="feature">
                <h2>{feature.title}</h2>
                <p>{feature.text}</p>
              </div>
            ))}
          </div>
        </div>

        {/* Right Side - Image */}
        <div className="features-image">
          <img src="/images/page3.png" alt="Features" className="feature-image" />
        </div>
      </div>

      {/* Login Popup */}
      {showLogin && <Login onClose={() => setShowLogin(false)} />} 

      {/* Signup Popup */}
      {showSignup && <Signup onClose={() => setShowSignup(false)} />} 
    </div>
  );
};

export default Home;
