import React, { useState } from "react";
import "./message.css";

const Chat = () => {
  const [messages, setMessages] = useState([
    { sender: "Ankush", text: "hi" },
    { sender: "Mani", text: "hi" },
  ]);
  const [newMessage, setNewMessage] = useState("");
  const [showPopup, setShowPopup] = useState(false);

  const handleSendMessage = () => {
    if (newMessage.trim() !== "") {
      setMessages([...messages, { sender: "You", text: newMessage }]);
      setNewMessage("");
    }
  };

  const handleOpenPopup = () => setShowPopup(true);
  const handleClosePopup = () => setShowPopup(false);

  return (
    <div className="chat-container">
      <div className="sidebar">
        <input type="text" placeholder="Chat or search" className="search-bar" />
        <button className="add-chat" onClick={handleOpenPopup}>+</button>
        <div className="chat-list">
          <div className="chat-item active">General Chat</div>
          <div className="chat-item">Group Chat #1</div>
          <div className="chat-item">Ankush (it's you)</div>
          <div className="chat-item">Mani</div>
        </div>
      </div>
      <div className="chat-main">
        <div className="chat-header">General Chat</div>
        <div className="chat-messages">
          {messages.map((msg, index) => (
            <div key={index} className="message">
              <span className="avatar">{msg.sender.slice(0, 2).toUpperCase()}</span>
              <span className="text">{msg.text}</span>
            </div>
          ))}
        </div>
        <div className="chat-input">
          <input
            type="text"
            placeholder="Type a message"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
          />
          <button onClick={handleSendMessage}>âž¤</button>
        </div>
      </div>
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <h2>Create Group</h2>
            <input type="text" placeholder="Group Name" />
            <input type="text" placeholder="Members" />
            <select>
              <option>Public</option>
              <option>Private</option>
            </select>
            <textarea placeholder="Description"></textarea>
            <button onClick={handleClosePopup}>Create Group</button>
            <button onClick={handleClosePopup} className="close-btn">Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Chat;
