import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaCopy } from "react-icons/fa";
import "./Otpverification.css";

const OTPVerification = ({ setIsVerified }) => {
  const [phone, setPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [generatedOtp, setGeneratedOtp] = useState(null);
  const [otpSent, setOtpSent] = useState(false);
  const [copied, setCopied] = useState(false);
  const navigate = useNavigate();

  const sendOtp = () => {
    if (phone.length !== 10 || !/^\d+$/.test(phone)) {
      alert("Enter a valid 10-digit phone number!");
      return;
    }

    const otpCode = Math.floor(100000 + Math.random() * 900000);
    setGeneratedOtp(otpCode);
    setOtpSent(true);
    setCopied(false); // Reset copied state
  };

  const copyOtp = () => {
    if (generatedOtp) {
      navigator.clipboard.writeText(generatedOtp);
      setCopied(true);
      setTimeout(() => setCopied(false), 1500);
    }
  };

  const verifyOtp = () => {
    if (otp === generatedOtp?.toString()) {
      alert("üéâ OTP Verified Successfully!");
      setIsVerified(true);
      navigate("/dashboard");
    } else {
      alert("‚ùå Invalid OTP! Try again.");
    }
  };

  return (
    <div className="otp-container">
      <h2>Phone Number Verification</h2>

      {!otpSent ? (
        <>
          <input
            type="tel"
            placeholder="Enter phone number (10 digits)"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
          <button className="send-btn" onClick={sendOtp}>Send OTP</button>
        </>
      ) : (
        <>
          {/* OTP Display with Copy Button */}
          <div className="otp-message">
            <p>OTP Sent: <strong>{generatedOtp}</strong></p>
            <FaCopy className="copy-icon" onClick={copyOtp} title="Copy OTP" />
            {copied && <span className="copied-message">Copied!</span>}
          </div>

          <input
            type="text"
            maxLength="6"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />
          <button className="verify-btn" onClick={verifyOtp}>Verify OTP</button>
        </>
      )}
    </div>
  );
};

export default OTPVerification;
