import React, { useState } from "react";
import { FaUser, FaEnvelope, FaLock, FaGoogle, FaGithub } from "react-icons/fa";
import "./signup.css";

export default function Signup() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [popupMessage, setPopupMessage] = useState("");

  const handleSignup = () => {
    if (name && email && password) {
      const user = { name, email, password };
      localStorage.setItem("user", JSON.stringify(user)); // Store user details
      setPopupMessage("✅ Signup Successful! Now login.");
    } else {
      setPopupMessage("⚠️ Please fill all fields!");
    }

    setTimeout(() => setPopupMessage(""), 2000); // Hide popup after 2 sec
  };

  return (
    <div className="form-container">
      <div className="form-card">
        <h2>Sign up an account</h2>

        <div className="social-login">
          <button className="social-btn google-btn">
            <FaGoogle className="icon" /> Google
          </button>
          <button className="social-btn github-btn">
            <FaGithub className="icon" /> GitHub
          </button>
        </div>

        <div className="separator"><span>or continue with email</span></div>

        <div className="input-group">
          <i><FaUser /></i>
          <input type="text" placeholder="Full Name" value={name} onChange={(e) => setName(e.target.value)} />
        </div>

        <div className="input-group">
          <i><FaEnvelope /></i>
          <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>

        <div className="input-group">
          <i><FaLock /></i>
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>

        <button className="signup-btn" onClick={handleSignup}>Sign Up</button>

        <p className="form-link">
          Already have an account? <a href="/">Log in</a>
        </p>

        {popupMessage && <div className="popup"><p>{popupMessage}</p></div>}
      </div>
    </div>
  );
}
