import React, { useState } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import "./Dashboard.css";

const Dashboard = () => {
  const [tasks, setTasks] = useState({
    new: [],
    review: [],
    completed: [],
  });

  const [newTask, setNewTask] = useState({ title: "", description: "" });

  // Handle Drag-and-Drop
  const onDragEnd = (result) => {
    const { source, destination } = result;
    if (!destination) return;

    const sourceColumn = source.droppableId;
    const destColumn = destination.droppableId;

    // Prevent dragging from Completed to other columns
    if (sourceColumn === "completed") return;

    // Moving task within the same column
    if (sourceColumn === destColumn) {
      const reorderedTasks = [...tasks[sourceColumn]];
      const [movedTask] = reorderedTasks.splice(source.index, 1);
      reorderedTasks.splice(destination.index, 0, movedTask);

      setTasks((prev) => ({
        ...prev,
        [sourceColumn]: reorderedTasks,
      }));
    } else {
      // Moving task between different columns
      const sourceTasks = [...tasks[sourceColumn]];
      const destTasks = [...tasks[destColumn]];
      const [movedTask] = sourceTasks.splice(source.index, 1);
      destTasks.splice(destination.index, 0, movedTask);

      setTasks((prev) => ({
        ...prev,
        [sourceColumn]: sourceTasks,
        [destColumn]: destTasks,
      }));
    }
  };

  // Add a new task to the "New" column
  const addTask = () => {
    if (newTask.title.trim() === "" || newTask.description.trim() === "") return;
    setTasks((prev) => ({
      ...prev,
      new: [...prev.new, { id: Date.now().toString(), ...newTask }],
    }));
    setNewTask({ title: "", description: "" });
  };

  return (
    <div className="dashboard">
      <div className="main-card">
        <h2>Task Management Board</h2>

        {/* Task Input Fields */}
        <div className="task-input">
          <input
            type="text"
            placeholder="Task Title"
            value={newTask.title}
            onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
          />
          <input
            type="text"
            placeholder="Task Description"
            value={newTask.description}
            onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
          />
          <button onClick={addTask}>+ Add Task</button>
        </div>

        <DragDropContext onDragEnd={onDragEnd}>
          <div className="board">
            {Object.keys(tasks).map((column) => (
              <Droppable key={column} droppableId={column}>
                {(provided) => (
                  <div
                    className="column"
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                  >
                    <div className="column-header">{column.toUpperCase()}</div>
                    <div className="task-list">
                      {tasks[column].map((task, index) => (
                        <Draggable
                          key={task.id}
                          draggableId={task.id}
                          index={index}
                          isDragDisabled={column === "completed"}
                        >
                          {(provided) => (
                            <div
                              className="task-item"
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              {...provided.dragHandleProps}
                            >
                              <h4>{task.title}</h4>
                              <p>{task.description}</p>
                            </div>
                          )}
                        </Draggable>
                      ))}
                      {provided.placeholder}
                    </div>
                  </div>
                )}
              </Droppable>
            ))}
          </div>
        </DragDropContext>
      </div>
    </div>
  );
};

export default Dashboard;
