import React from "react";
import "./home.css";

// Import images using public URL approach
const image = process.env.PUBLIC_URL + "/images/image.png";
const card1 = process.env.PUBLIC_URL + "/images/card1.png";
const card2 = process.env.PUBLIC_URL + "/images/card2.png";
const card3 = process.env.PUBLIC_URL + "/images/card3.png";
const page3 = process.env.PUBLIC_URL + "/images/page3.png";

const Home = () => {
  return (
    <div className="home-container">
      {/* Header Section */}
      <section className="header-section flex flex-col md:flex-row items-center p-10">
        <div className="text-content md:w-1/2 p-5">
          <h1 className="text-5xl font-bold text-gray-800">Your Productivity Hub</h1>
          <p className="text-lg text-gray-600 mt-4">
            Stay organized, track progress, and boost productivity with our task management system.
          </p>
          <button className="mt-6 px-6 py-2 bg-blue-600 text-white font-semibold rounded-lg shadow-md hover:bg-blue-700">
            Get Started
          </button>
        </div>
        <div className="image-content md:w-1/2 flex justify-center">
          <img src={image} alt="Task Management Illustration" className="w-full h-auto object-cover" />
        </div>
      </section>

      {/* Features Section */}
      <section className="features-section flex flex-wrap justify-center gap-10 p-10">
        <div className="feature-card bg-white shadow-lg rounded-lg p-6 w-80">
          <img src={card1} alt="Feature 1" className="w-full h-40 object-cover rounded-md" />
          <h3 className="text-xl font-semibold mt-4">Task Organization</h3>
          <p className="text-gray-600 mt-2">Easily manage and categorize tasks for better efficiency.</p>
        </div>
        <div className="feature-card bg-white shadow-lg rounded-lg p-6 w-80">
          <img src={card2} alt="Feature 2" className="w-full h-40 object-cover rounded-md" />
          <h3 className="text-xl font-semibold mt-4">Time Tracking</h3>
          <p className="text-gray-600 mt-2">Keep track of your work hours and improve time management.</p>
        </div>
        <div className="feature-card bg-white shadow-lg rounded-lg p-6 w-80">
          <img src={card3} alt="Feature 3" className="w-full h-40 object-cover rounded-md" />
          <h3 className="text-xl font-semibold mt-4">Collaboration</h3>
          <p className="text-gray-600 mt-2">Work together with your team seamlessly.</p>
        </div>
      </section>

      {/* Call to Action */}
      <section className="cta-section text-center p-10 bg-blue-100 rounded-lg mt-10">
        <h2 className="text-3xl font-bold text-gray-800">Boost Your Productivity Today!</h2>
        <p className="text-lg text-gray-600 mt-3">Sign up now and start organizing your work effectively.</p>
        <img src={page3} alt="Call to Action" className="mx-auto mt-6 w-60" />
        <button className="mt-6 px-6 py-2 bg-blue-600 text-white font-semibold rounded-lg shadow-md hover:bg-blue-700">
          Join Now
        </button>
      </section>
    </div>
  );
};

export default Home;
