import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Otpverification.css";

const OTPVerification = () => {
  const [phone, setPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [generatedOtp, setGeneratedOtp] = useState(null);
  const [otpSent, setOtpSent] = useState(false);
  const navigate = useNavigate();

  // Function to Generate and "Send" OTP (Simulated)
  const sendOtp = () => {
    if (phone.length !== 10) {
      alert("Enter a valid 10-digit phone number!");
      return;
    }

    const otp = Math.floor(100000 + Math.random() * 900000); // Generate random 6-digit OTP
    setGeneratedOtp(otp); // Store OTP in state
    setOtpSent(true);

    alert(`OTP Sent: ${otp} (Simulated) üöÄ`); // Simulating OTP sent
  };

  // Function to Verify OTP
  const verifyOtp = () => {
    if (otp === generatedOtp?.toString()) {
      alert("üéâ OTP Verified Successfully!");
      navigate("/"); // Redirect to home/dashboard
    } else {
      alert("‚ùå Invalid OTP! Try again.");
    }
  };

  return (
    <div className="otp-container">
      <h2>Phone Number Verification</h2>

      {!otpSent ? (
        <>
          <input
            type="tel"
            placeholder="Enter phone number (10 digits)"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
          <button className="send-btn" onClick={sendOtp}>Send OTP</button>
        </>
      ) : (
        <>
          <input
            type="text"
            maxLength="6"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />
          <button className="verify-btn" onClick={verifyOtp}>Verify OTP</button>
        </>
      )}
    </div>
  );
};

export default OTPVerification;
