import React, { useState } from "react";
import { Link } from "react-router-dom";
import { FaGoogle, FaGithub } from "react-icons/fa";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLoginClick = async () => {
    if (!email || !password) {
      setError("All fields are required");
      return;
    }
    // Implement login logic here
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white shadow-lg rounded-lg p-8 w-full max-w-md">
        <button className="text-gray-500 text-sm mb-4">&larr; Back</button>
        <h2 className="text-2xl font-semibold text-center mb-6">Log in to Account</h2>

        <div className="flex gap-4 justify-center mb-4">
          <button className="flex items-center px-4 py-2 bg-gray-200 rounded-md">
            <FaGoogle className="mr-2" /> Google
          </button>
          <button className="flex items-center px-4 py-2 bg-gray-200 rounded-md">
            <FaGithub className="mr-2" /> Github
          </button>
        </div>

        <div className="text-center text-gray-500 mb-4">or continue with email</div>

        <input
          type="email"
          placeholder="Email"
          className="w-full px-4 py-2 border rounded-md mb-2"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          className="w-full px-4 py-2 border rounded-md mb-2"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        <div className="flex justify-between text-sm text-gray-600 mb-4">
          <label>
            <input type="checkbox" className="mr-2" />
            Remember me
          </label>
          <a href="#" className="text-blue-500">Forgot Password?</a>
        </div>

        <button onClick={handleLoginClick} className="w-full bg-blue-600 text-white py-2 rounded-md">
          Log In
        </button>

        <p className="text-center text-gray-600 text-sm mt-4">
          Don't have an account? <Link to="/register" className="text-blue-500">Create an account</Link>
        </p>

        {error && <p className="text-red-500 text-sm mt-2 text-center">{error}</p>}
      </div>
    </div>
  );
}
