import React, { useState } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import "./dashboard.css";

const initialData = {
  columns: {
    new: { name: "New", items: [{ id: "1", content: "Event plans" }] },
    inProgress: { name: "In Progress", items: [{ id: "2", content: "Event plans (Today 1:53)" }] },
    event: { name: "Event", items: [] },
  },
};

const Dashboard = () => {
  const [data, setData] = useState(initialData);

  const onDragEnd = (result) => {
    if (!result.destination) return;
    
    const { source, destination } = result;
    const sourceColumn = data.columns[source.droppableId];
    const destColumn = data.columns[destination.droppableId];
    const sourceItems = [...sourceColumn.items];
    const destItems = [...destColumn.items];
    const [movedItem] = sourceItems.splice(source.index, 1);
    destItems.splice(destination.index, 0, movedItem);

    setData({
      columns: {
        ...data.columns,
        [source.droppableId]: { ...sourceColumn, items: sourceItems },
        [destination.droppableId]: { ...destColumn, items: destItems },
      },
    });
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h2>Zidio</h2>
        <nav>
          <button className="nav-btn">People</button>
          <button className="nav-btn">Report</button>
          <button className="nav-btn active">In Progress</button>
          <input type="text" className="search-input" placeholder="Search" />
          <button className="invite-btn">Invite</button>
        </nav>
      </header>
      
      <DragDropContext onDragEnd={onDragEnd}>
        <div className="kanban-board">
          {Object.entries(data.columns).map(([columnId, column]) => (
            <div key={columnId} className="kanban-column">
              <h3 className="column-title">{column.name}</h3>
              <Droppable droppableId={columnId}>
                {(provided) => (
                  <div ref={provided.innerRef} {...provided.droppableProps} className="kanban-items">
                    {column.items.map((item, index) => (
                      <Draggable key={item.id} draggableId={item.id} index={index}>
                        {(provided) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            className="kanban-item"
                          >
                            {item.content}
                          </div>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            </div>
          ))}
        </div>
      </DragDropContext>
    </div>
  );
};

export default Dashboard;
