import React, { useState } from "react";
import Navbar from "../navbar/Navbar";
import Login from "../login/Login";
import Signup from "../signup/Signup";
import "./home.css";

const Home = () => {
  const [showLogin, setShowLogin] = useState(false);
  const [showSignup, setShowSignup] = useState(false);
  const [openFAQ, setOpenFAQ] = useState(null);

  const toggleFAQ = (index) => {
    setOpenFAQ(openFAQ === index ? null : index);
  };

  return (
    <div className="home-container">
      <Navbar onLoginClick={() => setShowLogin(true)} onSignupClick={() => setShowSignup(true)} />
      
      <div className="home-content">
        <div className="text-container">
          <h1>Transform Task Management with Zidio</h1>
          <p>
            Harness Zidio Task Management to enhance efficiency and collaboration. Our intuitive platform allows for effortless task organization, real-time progress tracking, and seamless teamwork, enabling your team to focus on achieving goals.
          </p>
          <button className="btn-secondary">Get Started</button>
        </div>
        <div className="image-container">
          <img src="/images/image.png" alt="Home" className="home-image" />
        </div>
      </div>

      {/* FAQ Section */}
      <div className="faq-section">
        <h1>Frequently Asked Questions</h1>
        <div className="faq-list">
          {[{
            question: "How can Zidio support my team's productivity?",
            answer: "Zidio helps streamline task organization, track progress, and enhance collaboration among teams."
          },
          {
            question: "What features make Zidio unique?",
            answer: "Zidio offers dynamic Kanban boards, real-time updates, and seamless integrations with popular tools."
          },
          {
            question: "How does Zidio drive teamwork?",
            answer: "By centralizing task management, providing transparency, and fostering collaboration among team members."
          },
          {
            question: "What support options do you offer?",
            answer: "We offer comprehensive online support and training materials for all users."
          },
          {
            question: "How can I get started with Zidio?",
            answer: "Simply sign up, choose your plan, and start using Zidio for effortless task management."
          }].map((faq, index) => (
            <div key={index} className="faq-item" onClick={() => toggleFAQ(index)}>
              <h3>{faq.question}</h3>
              {openFAQ === index && <p className="faq-answer">{faq.answer}</p>}
            </div>
          ))}
        </div>
      </div>

      {/* Login Popup */}
      {showLogin && <Login onClose={() => setShowLogin(false)} />}

      {/* Signup Popup */}
      {showSignup && <Signup onClose={() => setShowSignup(false)} />}
    </div>
  );
};

export default Home;
