import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import Login from "./components/login/Login";
import Signup from "./components/signup/Signup";
import OTPVerification from "./components/otpverification/OTPVerification";
import Dashboard from "./components/dashboard/Dashboard";
import Message from "./components/message/Message";
import Home from "./components/home/Home";
import Navbar from "./components/navbar/Navbar"; // Import Navbar

function App() {
  const [isVerified, setIsVerified] = useState(false); // Track OTP verification
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Track login state

  // Check if user is already logged in
  useEffect(() => {
    const userData = localStorage.getItem("userData");
    if (userData) {
      setIsAuthenticated(true);
    }
  }, []);

  return (
    <Router>
      {/* Show Navbar with login/signup if NOT authenticated, otherwise show full navbar */}
      {isAuthenticated && isVerified ? <Navbar /> : <Navbar showAuthButtons />}

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
        <Route path="/otp" element={<OTPVerification setIsVerified={setIsVerified} />} />
        <Route 
          path="/dashboard" 
          element={isAuthenticated && isVerified ? <Dashboard /> : <Navigate to="/login" />} 
        />
        <Route path="/messages" element={isAuthenticated && isVerified ? <Message /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
