import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./login.css";
import { FaGoogle, FaGithub } from "react-icons/fa";
import { signInWithGoogle, signInWithGithub } from "../../firebase/firebaseConfig.js";  // Adjust the import path

const Login = () => {
  const [popupMessage, setPopupMessage] = useState("");
  const navigate = useNavigate();

  const handleGoogleLogin = async () => {
    const user = await signInWithGoogle();
    if (user) {
      setPopupMessage(`✅ Welcome ${user.displayName}! Redirecting...`);
      setTimeout(() => navigate("/dashboard"), 2000);
    }
  };

  const handleGithubLogin = async () => {
    const user = await signInWithGithub();
    if (user) {
      setPopupMessage(`✅ Welcome ${user.displayName}! Redirecting...`);
      setTimeout(() => navigate("/dashboard"), 2000);
    }
  };

  return (
    <div className="login-card">
      <h2>Login to Your Account</h2>

      <div className="social-login">
        <button className="social-btn google-btn" onClick={handleGoogleLogin}>
          <FaGoogle className="icon" /> Google
        </button>
        <button className="social-btn github-btn" onClick={handleGithubLogin}>
          <FaGithub className="icon" /> GitHub
        </button>
      </div>

      {popupMessage && <div className="popup"><p>{popupMessage}</p></div>}
    </div>
  );
};

export default Login;
