import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./login.css";
import { FaGoogle, FaGithub } from "react-icons/fa";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [showPopup, setShowPopup] = useState(false);
  const navigate = useNavigate();

  const handleLogin = () => {
    const registeredUsers = JSON.parse(localStorage.getItem("users")) || [];
    const userExists = registeredUsers.find((user) => user.email === email);

    if (!userExists) {
      setError("Email is not registered. Please sign up first.");
      return;
    }
    
    if (userExists.password !== password) {
      setError("Incorrect password. Please try again.");
      return;
    }
    
    setShowPopup(true);
    setTimeout(() => {
      setShowPopup(false);
      navigate("/otp");
    }, 2000);
  };

  return (
    <div className="login-card">
      <h2>Login to Your Account</h2>

      <div className="social-login">
        <button className="social-btn google-btn">
          <FaGoogle className="icon" /> Google
        </button>
        <button className="social-btn github-btn">
          <FaGithub className="icon" /> GitHub
        </button>
      </div>

      <div className="separator"><span>or continue with email</span></div>

      {error && <p className="error-message">{error}</p>}

      <div className="input-group">
        <label>Email</label>
        <input 
          type="email" 
          placeholder="Enter your email" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>

      <div className="input-group">
        <label>Password</label>
        <input 
          type="password" 
          placeholder="Enter your password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>

      <div className="options">
        <label>
          <input type="checkbox" /> Remember me
        </label>
        <a href="#">Forgot Password?</a>
      </div>

      <button className="login-btn" onClick={handleLogin}>Login</button>

      <p className="create-account">
        Don't have an account? <a href="/signup">Sign up</a>
      </p>

      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <h3>âœ… Login Successful!</h3>
            <p>Redirecting to OTP verification...</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Login;
