import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FaUserCircle } from "react-icons/fa";
import "./navbar.css";

const Navbar = () => {
  const [fullName, setFullName] = useState("");
  const [showDropdown, setShowDropdown] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("userData"));
    if (userData) {
      setFullName(userData.fullName || "User");
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("userData");
    navigate("/");
  };

  return (
    <nav className="navbar">
      <div className="nav-right">
        <div className="user-icon-container">
          <FaUserCircle
            className="user-icon"
            title="User Profile"
            onClick={() => setShowDropdown(!showDropdown)}
          />
          {showDropdown && (
            <div className="dropdown-menu user-dropdown">
              <p className="dropdown-item">Hi, {fullName}</p> {/* Displays full name */}
              <hr />
              <p className="dropdown-item" onClick={() => navigate("/settings")}>Settings</p>
              <p className="dropdown-item logout" onClick={handleLogout}>Logout</p>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
