import React, { useState } from "react";
import Navbar from "../navbar/Navbar";
import Login from "../login/Login";
import Signup from "../signup/Signup";
import "./home.css";

const Home = () => {
  const [showLogin, setShowLogin] = useState(false);
  const [showSignup, setShowSignup] = useState(false);

  return (
    <div className="home-container">
      {/* Navbar with Login & Signup Buttons */}
      <Navbar
        onLoginClick={() => setShowLogin(true)}
        onSignupClick={() => setShowSignup(true)}
      />

      {/* Home Content */}
      <div className="content">
        <h1>Transform Task Management with Zidio</h1><br/> 
        <p>
          Harness Zidio Task Management to enhance efficiency and collaboration. 
          Our intuitive platform allows <br/>for effortless task organization, <br/> 
          real-time progress tracking, and seamless teamwork.
        </p>
        <button className="btn-secondary">Get Started</button>
      </div>

      {/* Image */}
      <div className="image-container">
        <img src="/images/image.png" alt="Home" className="home-image" />
      </div>

      {/* Feature Section */}
      <div className="features-section">
        <h2 className="features-header">
          Experience the Transformative Capabilities of Zidio Task Management
        </h2>

        <div className="cards-container">
          {/* Card 1 - Kanban Boards */}
          <div className="card">
            <img
              src="/images/card1.png"
              alt="Kanban Boards"
              className="card-image"
            />
            <h3>Robust Task Organization with Kanban Boards</h3>
            <p>
              Visualize your workflows and prioritize tasks effectively, 
              ensuring your team is always aligned.
            </p>
          </div>

          {/* Card 2 - Progress Tracking */}
          <div className="card">
            <img
              src="/images/card2.png"
              alt="Progress Tracking"
              className="card-image"
            />
            <h3>Real-Time Progress Tracking</h3>
            <p>
              Stay up-to-date with your projects and make swift adjustments 
              to keep everything on course.
            </p>
          </div>

          {/* Card 3 - Seamless Integrations */}
          <div className="card">
            <img
              src="/images/card3.png"
              alt="Seamless Integrations"
              className="card-image"
            />
            <h3>Seamless Integrations</h3>
            <p>
              Integrate with the tools you already use, simplifying your 
              workflow and reducing app-switching.
            </p>
          </div>
        </div>
      </div>
      

      {/* Login Popup */}
      {showLogin && <Login onClose={() => setShowLogin(false)} />}

      {/* Signup Popup */}
      {showSignup && <Signup onClose={() => setShowSignup(false)} />}
    </div>
  );
};

export default Home;
